<?php// Include config filerequire_once "databaseconfig.php";$link = mysqli_connect(HOST, USERNAME, PASSWORD, DBNAM);// Check connectionif($link === false){    die("ERROR: Could not connect. " . mysqli_connect_error());}$username = $password = $confirm_password = "";$username_err = $password_err = $confirm_password_err = "";if($_SERVER["REQUEST_METHOD"] == "POST"){    if(empty(trim($_POST["username"]))){        $username_err = "Please enter a username.";    } else{        $sql = "SELECT id FROM users WHERE username = ?";        if($stmt = mysqli_prepare($link, $sql)){            mysqli_stmt_bind_param($stmt, "s", $param_username);            $param_username = trim($_POST["username"]);            if(mysqli_stmt_execute($stmt)){                mysqli_stmt_store_result($stmt);                if(mysqli_stmt_num_rows($stmt) == 1){                    $username_err = "This username is already taken.";                } else{                    $username = trim($_POST["username"]);                }            } else{                echo "Oops! Something went wrong. Please try again later.";            }        }        mysqli_stmt_close($stmt);    }    if(empty(trim($_POST["password"]))){        $password_err = "Please enter a password.";    } elseif(strlen(trim($_POST["password"])) < 6){        $password_err = "Password must have atleast 6 characters.";    } else{        $password = trim($_POST["password"]);    }    if(empty(trim($_POST["confirm_password"]))){        $confirm_password_err = "Please confirm password.";    } else{        $confirm_password = trim($_POST["confirm_password"]);        if(empty($password_err) && ($password != $confirm_password)){            $confirm_password_err = "Password did not match.";        }    }     if(empty($username_err) && empty($password_err) && empty($confirm_password_err)){        // Prepare an insert statement        $sql = "INSERT INTO users (username, password) VALUES (?, ?)";        if($stmt = mysqli_prepare($link, $sql)){            mysqli_stmt_bind_param($stmt, "ss", $param_username, $param_password);            $param_username = $username;            $param_password = password_hash($password, PASSWORD_DEFAULT); // Creates a password hash            if(mysqli_stmt_execute($stmt)){                // Redirect to login page                header("location: login.php");            } else{                echo "Something went wrong. Please try again later.";            }        }        // Close statement        mysqli_stmt_close($stmt);    }    // Close connection    mysqli_close($link);}?><html><head>    <title>Sign up</title>    <meta name="viewport" content="width=device-width, initial-scale=1">    <link rel="stylesheet" href="css3.css"></head><body><div class="modal"> <span  class="close" title="Close Modal"><button class="close"><a href="index.php">&times;</a></button> </span> <form class="modal-content" action="<?php htmlspecialchars($_SERVER["PHP_SELF"]); ?>" method="post">  <div class="container">   <h1>Sign Up</h1>   <p>Please fill in this form to create an account.</p>   <hr>      <div  <?php echo (!empty($username_err)) ? 'has-error' : ''; ?>">   <label><b>Username</b></label>      <span class="red"><?php echo $username_err; ?></span>   <input type="text" placeholder="Enter Surname" name="username" >  </div>     <div class="form-group <?php echo (!empty($password_err)) ? 'has-error' : ''; ?>">   <label><b>Password</b></label>   <input type="password" placeholder="password" name="password" >         <span class="red"><?php echo $password_err; ?></span></div>     <div class="form-group <?php echo (!empty($confirm_password_err)) ? 'has-error' : ''; ?>">   <label><b>Repeat Password</b></label><span class="red"><?php echo $confirm_password_err; ?></span>   <input type="password" placeholder="Confirm Password" name="confirm_password" >     </div>   <p>By creating an account you